mapper.update:
update member
member_name = #{memberName}, member_level = #{memberLevel}, member_email = #{memberEmail},
member_post = #{memberPost}, member_address1 = #{memberAddress1}, member_address2 = #{memberAddress2},
member_contact = #{memberContact}, member_birth = #{memberBirth}, member_point = #{memberPoint}
where member_id = #{memberId}

